import React, { useState } from 'react';
import { Button, Input, Box, Text, Spinner } from "@chakra-ui/react";

function PlayerSearch() {
  const [playerId, setPlayerId] = useState('');
  const [playerData, setPlayerData] = useState(null);

  const searchPlayer = async () => {
    const res = await fetch(`http://localhost:3001/scrape/${encodeURIComponent(playerId)}`);
    const data = await res.json();
    setPlayerData(data[0]);  // Only take the first player in the response
  };

  return (
    <>
      <Input value={playerId} onChange={e => setPlayerId(e.target.value)} />
      <Button onClick={searchPlayer}>Search</Button>
      {playerData && (
        <Box borderWidth={1} borderRadius="lg" overflow="hidden" padding={4} marginTop={4}>
          <Text fontSize="xl" marginBottom={2}>Player: {playerData.name}</Text>
          <Text>Games Played: {playerData.games}</Text>
          <Text>Goals Scored: {playerData.goals}</Text>
        </Box>
      )}
    </>
  );
}

export default PlayerSearch;
